    boolean searchButtonPressed = false, resultFound = false;
    Student student;
    
    private void modifyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modifyActionPerformed
        if(searchButtonPressed)
        {
            if(resultFound)
            {
                student = new Student(ID.getText(), fullName.getText(),age.getText(),contact.getText(),(String) source.getSelectedItem(),(String) destination.getSelectedItem(),(String) flightClass.getSelectedItem());
                
                DataHandler.delete(student.ID);
                DataHandler.insert(student);
                JOptionPane.showMessageDialog(null,"Booking Modified Sucessfully!");
                setEmpty();
            }
        }else
        {
            JOptionPane.showMessageDialog(null, "Search Booking to Modify them!");
        }
    }//GEN-LAST:event_modifyActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        IDNumber.setText(null);
    }//GEN-LAST:event_jButton1ActionPerformed

    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        searchButtonPressed = false;
        resultFound = false;
        if(!IDNumber.getText().isEmpty())
        {
            searchButtonPressed = true;
            Node result = DataHandler.search(Integer.parseInt(IDNumber.getText()));
            if(result == null)
            {
                JOptionPane.showMessageDialog(null, "No Result Found!");
                resultFound = false;
            }else
            {
                resultFound = true;
                student = new Student(result);
                this.fullName.setText(student.fullName);
                this.age.setText(String.valueOf(student.age));
                this.contact.setText(String.valueOf(student.contact));
                this.source.setSelectedItem(student.department);
                this.destination.setSelectedItem(student.semester);
                this.flightClass.setSelectedItem(student.section);
                this.ID.setText(String.valueOf(student.ID));
            }
        }else
        {
            JOptionPane.showMessageDialog(null, "Enter Nic Number to Search!");
        }
    }//GEN-LAST:event_searchActionPerformed

