    private void setEmpty()
    {
        IDNumber.setText(null);
        fullName.setText(null);
        age.setText(null);
        contact.setText(null);
        source.setSelectedIndex(0);
        destination.setSelectedIndex(0);
        flightClass.setSelectedIndex(0);
        ID.setText(null);
    }
